/* 
Resource: https://youtu.be/0YvPgYDR1oM
*/

// 3rd party library imports
import P5 from 'p5';
import * as Tone from 'tone';

// project imports
import { Visualizer } from '../Visualizers';

var start = 0;

export const HadiVisualizer = new Visualizer(
    'Drums',
    (p5: P5, analyzer: Tone.Analyser) => {
        const width = window.innerWidth;
        const height = window.innerHeight / 2;


        /*
        Code to make gradient background color of visualizer.
        Resource: Team Member - Darren
        */
        let color1 = p5.color(139, 210, 236);
        let color2 = p5.color(146, 206, 168);

        for (let y = 0; y < height; y++) {
            let n = p5.map(y, 0, height, 0, 1);
            let newColor = p5.lerpColor(color1, color2, n);
            p5.stroke(newColor);
            p5.line(0, y, width, y);
        }


        //center circle
        p5.translate(width / 2.5, height / 2);
        p5.noiseDetail(2, 1);

        const values = analyzer.getValue();
        p5.beginShape();

        // my new visualizer

        var space = 1

        for (let i = 0; i < 180; i += space) {
            var amplitude = values[i] as number;
            var my_amp = Math.abs(amplitude)

            // gives constant movement
            var xoff = p5.map(Math.cos(i), 0, 1, 0, my_amp * 10)
            var yoff = p5.map(Math.sin(i), 0, 1, 0, my_amp * 10)
            var n = p5.noise(xoff + start, yoff + start) * (my_amp * 5)
            // rectangle dims
            var h = p5.map(n, 0, 1, 0, 200)
            var w = 3

            // colors
            var r = p5.map(Math.sin(i), 0, 1, 100, my_amp * 150 + 147)
            var g = p5.map(i / 4, -100, 100, 0, my_amp * 150 + 112)
            var b = p5.map(n, 0, 1, 175, my_amp * 150 + 219)
            p5.stroke(r, g, b);
            p5.fill(r, g, b);

            // drawing the circle visual
            p5.rotate(space);
            p5.rect(90, 0, h, w);



        }
        start += 0.03;

        p5.endShape();

    },

);
