// 3rd party library imports
import P5 from 'p5';
import * as Tone from 'tone';

// project imports
import { Visualizer } from '../Visualizers';


export const HadiVisualizer = new Visualizer(
    'Drums',
    (p5: P5, analyzer: Tone.Analyser) => {
        const width = window.innerWidth;
        const height = window.innerHeight / 2;
        const dim = Math.min(width, height);

        /*
        Code to make gradient background color of visualizer.
        Resource: Team Member - Darren
        */
        let color1 = p5.color(139, 210, 236);
        let color2 = p5.color(146, 206, 168);

        for (let y = 0; y < height; y++) {
            let n = p5.map(y, 0, height, 0, 1);
            let newColor = p5.lerpColor(color1, color2, n);
            p5.stroke(newColor);
            p5.line(0, y, width, y);
        }

        p5.strokeWeight(dim * 0.01);
        p5.stroke(255, 255, 255, 255);
        p5.noFill();
        p5.CURVE;

        const values = analyzer.getValue();
        p5.beginShape();
        for (let i = 0; i < values.length; i++) {
          const amplitude = values[i] as number;
          const x = p5.map(i, 0, values.length - 1, 0, width);
          const y = height / 2 + amplitude * height;
          // Place vertex
          p5.vertex(x, y);
        }
        p5.endShape();
    },

);
